import { useState, useEffect } from 'react';
import quotesData from './assets/quotes.json';
import './App.css';

function App() {

  const [quotes, setQuotes] = useState([]);
  // const [idFilter, setIdFilter] = useState("");
  const [quoteFilter, setQuoteFilter] = useState("");
  const [authoerFilter, setAuthorFilter] = useState("");

  useEffect(() => {
    setQuotes(quotesData.quotes);
  }, []);

  const handleQuoteFilterChange = (e) => (
    setQuoteFilter(e.target.value)
  );

  const filteredQuotes = quotes.filter(q => (
    q.quote.toLowerCase().includes(quoteFilter.toLowerCase())
  ));

  // const filteredQuotes = quotes.filter(q => (
  //   q.quote.toLowerCase().includes(quotesFilter.toLowerCase())
  //   && q.author.toLowerCase().includes(authorFilter.toLowerCase())
  // ));

  return (
    <div className="App">
      <h2>Quotes</h2>
      <form>
        <label htmlFor="quoteFilterInput">Quote: </label>
        <input
          id="quoteFilterInput"
          value={quoteFilter}
          onChange={handleQuoteFilterChange}
        />
      </form>

      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Quote</th>
            <th>Author</th>
          </tr>
        </thead>
        <tbody>
          {filteredQuotes.map((q) => (
            <tr key={q.id}>
              <td>{q.id}</td>
              <td>{q.quote}</td>
              <td>{q.author}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default App;
