import { useState, useEffect } from 'react';
import quotesData from './assets/quotes.json';
import './App.css';

function App() {

  const [quotes, setQuotes] = useState([]);
  const [quotesFilter, setQuotesFilter] = useState("");
  const [authorFilter, setAuthorFilter] = useState("");

  useEffect(() => {
    setQuotes(quotesData.quotes);
  }, []);

  const handleQuotesFilterChange = (e) => (
    setQuotesFilter(e.target.value)
  );

  const handleAuthorFilterChange = (e) => (
    setAuthorFilter(e.target.value)
  );

  const filteredQuotes = quotes.filter(q => (
    q.quote.toLowerCase().includes(quotesFilter.toLowerCase())
  ));

  return (
    <div className="App">
      <h2>Quotes</h2>
      <form>
        <label htmlFor="quoteFilter">Quote: </label>
        <input
          id="quoteFilter"
          value={quotesFilter}
          onChange={handleQuotesFilterChange}
        />
      </form>
      <form>
        <lable></lable>
        <input
          id="authorFilter"
          value={authorFilter}
          onChange={handleAuthorFilterChange}
        />
      </form>

      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Quote</th>
            <th>Author</th>
          </tr>
        </thead>
        <tbody>
          {filteredQuotes.map((q) => (
            <tr key={q.id}>
              <td>{q.id}</td>
              <td>{q.quote}</td>
              <td>{q.author}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default App;
