import { useState, useEffect } from 'react';
import quotesData from './assets/quotes.json';
import './App.css';

function App() {

  return (
    <div className="App">
      <h2>Quotes</h2>
      <form>
        <label htmlFor="quoteFilter">Quote: </label>
        <input
          id="quoteFilter"
          value={quotesFilter}
          onChange={handleQuotesFilterChange}
        />
      </form>
      <form>
        <lable htmlFor="authorFilter">Author: </lable>
        <input
          id="authorFilter"
          value={authorFilter}
          onChange={handleAuthorFilterChange}
        />
      </form>

      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Quote</th>
            <th>Author</th>
          </tr>
        </thead>
        <tbody>
          {filteredQuotes.map((q) => (
            <tr key={q.id}>
              <td>{q.id}</td>
              <td>{q.quote}</td>
              <td>{q.author}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default App;
