import { useState, useEffect } from 'react';
import quotesData from './assets/quotes.json';
import './App.css';

function App() {

  const [quotes, setQuotes] = useState([]);
  const [quotesFilter, setQuotesFilter] = useState("");

  useEffect(() => {
    setQuotes(quotesData.quotes);
  }, []);

  handleQuotesFilterChange(() => {
    if (quotesFilter.length > 0) {
      setQuotes(quotes.filter(quoteData => quoteData.quote.contains(quotesFilter)));
    } else {
      setQuotes(quotesData.quotes);
    }
  });

  return (
    <div className="App">
      <h2>Quotes</h2>
      <form>
        <label htmlFor="quoteFilter">Quote: </label>
        <input
          id="quoteFilter"
          value={quotesFilter}
          onChange={handleQuotesFilterChange}
        />
      </form>

      <table>

      </table>
    </div>
  );
}

export default App;
