import { useState, useEffect } from 'react';
import quotesData from './assets/quotes.json';
import './App.css';

function App() {

  const [quotes, setQuotes] = useState([]);
  const [idFilter, setIdFilter] = useState("");
  const [quoteFilter, setQuoteFilter] = useState("");
  const [authoerFilter, setAuthorFilter] = useState("");

  useEffect(() => {
    setQuotes(quotesData.quotes);
  }, []);

  const handleIdFilterChange = (e) => (
    setIdFilter(e.target.value)
  );

  const filteredQuotes = (() => (
    quotes.filter(q => q.id.includes(idFilter))
  ));

  return (
    <div className="App">
      <h2>Quotes</h2>
      <form>
        <label htmlFor="idFilterInput">ID</label>
        <input
          id="idFilterInput"
          value={idFilter}
          onChange={handleIdFilterChange}
        />
      </form>

      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Quote</th>
            <th>Author</th>
          </tr>
        </thead>
        <tbody>
          {}
        </tbody>
      </table>
    </div>
  );
}

export default App;
